Game.create!([
  {title: "Генератор ников для  World of Warcraft (WoW РП ники и имена)", name: "World of Warcraft", content: "Наверняка многие тысячи игроков сталкивались с выбором имени игрового персонажа. Это не всегда легко - подобрать своему аватару, альтер-эго в игровом мире подходящее ему, вам и вселенной игры имя. Генератор ников отчасти решает эту проблему - с его помощью можно сгенерировать ролевой (рп) ник для WoW, причем суммарно может быть сгенерировано несколько миллионов вариантов - есть где разгуляться!\r\n\r\nГенератор разбит на расы-категории, а поскольку расы в игре World of Warcraft довольно сильно различаются, то и создаваемые имена будут выглядеть соответственно духу и атмосферности выбранной игровой расы.", slug: "wow", description: "Онлайн генератор имен и ников для World of Warcraft"},
  {title: "Генератор ников для Guild Wars (РП ники и имена)", name: "Guild Wars", content: "В мире Тирии обитает множество представителей различных рас, и пять из них доступны для активной игры. Гордые люди, изобретательные Асура, умиротворенные Сильвари, воинственные Чарры и суровые Норны - у каждой из этих рас свои собственные имена, и чтобы получить максимальное удовольствие от игры, важно соблюдать её атмосферу. Назовите своего Норна поистине северным именем, которое прославит его далеко за пределами родных снегов или подберите никнейм для любого из ваших персонажей!", slug: "gw2", description: "Онлайн генератор имен и ников для Guild Wars"},
  {title: "Генератор ников для GTA San Andreas Multiplayer", name: "San Andreas Multiplayer", content: "Игровой мир GTA San Andreas Multiplayer основан на реальных городах, в которых живут реальные люди множества различных национальностей. Здесь есть немцы, французы, итальянцы и, конечно же, американцы. Для полного погружения в атмосферу игры вам потребуется ролевой (РП) ник для GTA SAMP. Здесь вы сможете сгенерировать себе красивое имя для любой национальности. Создавайте себе мужские и женские имена и фамилии для ваших персонажей!", slug: "samp", description: "Онлайн генератор имен и ников для GTA San Andreas"},
  {title: "Генератор ников по скинам для Minecraft", name: "Minecraft", content: "Генератор ников по скинам для Minecraft позволит подобрать ник или имя на любой вкус и цвет. Кроме того, помимо простого создания ника Nickshaker предлагает своим пользователям создавать ники вместе со скинами. Это очень просто: нажмите кнопку для получения ника и посмотрите, нравится ли вам скин, который будет на вас в игре, если вы зайдете в Minecraft с таким ником. Если скин вам понравился, запомните ник и войдите в игру, указав его при в ходе и на вашем персонаже появится этот скин!", slug: "minecraft", description: "Онлайн генератор имен и ников для Minecraft, ники по скинам"}
])

Race.create!([
  {title: "Генератор ников для Орков(РП ники, генератор roleplay ников и имен)", name: "Генератор ников для Орка", content: "Орки - пришельцы из мира Драенор, сейчас более известного как Запределье. Несмотря на изначальную приближенность к духам природы, исторически сложилось так, что орки стали известны в качестве кровожадных и неустрашимых воителей. Имя орка звучит грубо, зачастую резко и не отличается гармонией и красотой.", slug: "orc", description: "", game: Game.where(slug: 'wow').first},
  {title: "Генератор ников для гномов(РП ники, генератор roleplay ников)", name: "Генератор ников для Гнома", content: "Гномы - искусные механики и инженеры, они обожают мастерить сложные устройства, что нередко приводит к различного масштаба разрушениям. Иногда гномы связывают свою жизнь с магией, что, однако, не мешает звучать их именам так, словно вы потрясли ведро с гайками и болтами.", slug: "gnome", description: "", game: Game.where(slug: 'wow').first},
  {title: "Генератор ников для дварфов(РП ники, генератор roleplay ников)", name: "Генератор ников для Дворфа", content: "Дворфы появились в Азероте с момента его сотворения. Изначально их раса называлась Земельниками, но эволюция сделала своё дело, и дворфы, кряжистые и несколько грубые создания, стали такими, какими мы знаем их сейчас. Дворфы являются умелыми мастерами горного, кузнечного и пивоваренного дела, поэтому многие из имен связаны с их ремеслом.", slug: "dwarf", description: "", game: Game.where(slug: 'wow').first},
  {title: "Генератор ников для Тауренов(РП ники, генератор roleplay ников и имен)", name: "Генератор ников для Таурена", content: "Таурены ничуть не меньше ночных эльфов тяготеют к природе. В основе их традиций лежит поклонение солнцу, природе и уважение предков, давших живущим представителям расы свою бесконечную мудрость. Имена тауренов просты и легко запоминаются. Возможно, именно поэтому они звучат довольно приятно, хотя порой несложно распознать и некую воинственность.", slug: "tauren", description: "", game: Game.where(slug: 'wow').first},
  {title: "Генератор ников для Ночных эльфов(РП ники, генератор roleplay ников и имен)", name: "Генератор ников для Ночного Эльфа", content: "Ночные эльфы - искусные друиды и священники, отличающиеся наибольшей близостью к природе, среди прочих рас. Звучание имен ночных эльфов приятно на слух - тяга этой расы к жизни и гармонии сделала их красивыми и запоминающимися. У эльфов нет бессмысленных имен - каждый звук имеет своё значение.", slug: "nightelf", description: "", game: Game.where(slug: 'wow').first},
  {title: "Генератор ников для Дренеев (РП ники, генератор roleplay ников и имен)", name: "Генератор ников для Дренеев", content: "Дренеи - раса космических беженцев, хранящая в своих заветах поклонение свету, веру в пророчества и почитание Наару - своих небесных покровителей. Имена дренеев традиционно содержат много гласных, что делает их непривычными для слуха коренных обитателей Азерота.", slug: "draenei", description: "", game: Game.where(slug: 'wow').first},
  {title: "Генератор ников для Воргенов(РП ники, генератор roleplay ников и имен)", name: "Генератор ников для Воргенов", content: "Напрасно Генн Седогрив ожидал, что его великая стена спасет народ Гилнеаса от любой опасности. Горькая судьба постигла жителей славного королевства несмотря на все надежды. Любой ворген - в первую очередь бывший представитель гордого королевства Гилнеас, а значит, ему пристало достойное имя. Имя, которое прославит его обладателя, несмотря на его проступающую волчью натуру.", slug: "worgen", description: "", game: Game.where(slug: 'wow').first},
  {title: "Генератор ников для Гоблинов(РП ники, генератор roleplay ников и имен)", name: "Генератор ников для Гоблинов", content: "То, что гоблины все еще не вымерли, можно объяснить лишь их потрясающей везучестью и свойственной им деловой жилкой. Большинство представителей этой расы оказываются довольно-таки жадными, как до денег, так и до взрывчатки. Если вашего собеседника зовут как-то вроде \"Златовзрыв\" - постарайтесь не иметь с ним дел. Это может оказаться чревато для вашего кошелька, здоровья или жизни. Или - для всего и сразу.", slug: "goblin", description: "", game: Game.where(slug: 'wow').first},
  {title: "Генератор ников для людей (человек)(РП ники, генератор roleplay ников)", name: "Генератор ников для Человека", content: "Люди - одна из наиболее молодых рас Азерота. Многие представители человеческой расы посвящают себя служению свету, магии или становятся доблестными воителями. Имена людей как правило просты, но неизменно несут в себе некий смысл. И ничего страшного, что со временем он позабылся или его на самом деле никогда не было.", slug: "human", description: "", game: Game.where(slug: 'wow').first},
  {title: "Генератор ников для Троллей(РП ники, генератор roleplay ников и имен)", name: "Генератор ников для Тролля", content: "Тролли - древняя воинственная раса, отличающася так же и уважением к традициям своего народа. Тролли недалеко продвинулись в технологиях, но чтут заветы своих шаманов, время от времени не забывая раскурить трубочку и поговорить о вуду. Имена троллей звучат порой грубовато, но не лишены некоторого шарма.", slug: "troll", description: "", game: Game.where(slug: 'wow').first},
  {title: "Генератор ников для нежити и отрекшихся(РП ники, генератор roleplay ников и имен)", name: "Генератор ников для Нежити", content: "Отрекшиеся - восставшие люди бывшего Лордаерона. В бытность свою людьми они, разумеется, носили человеческие имена, которые сейчас, возможно, звучат слегка старомодно. Однако, это лишь придает существованию верных служителей королевы баньши некоторую горечь, не дающую позабыть об их прошлом - ведь когда-то все они были людьми.", slug: "undead", description: "", game: Game.where(slug: 'wow').first},
  {title: "Генератор ников для эльфов крови(РП ники, генератор roleplay ников и имен, кровавые эльфы, блад эльфы)", name: "Генератор ников для Эльфов Крови", content: "Эльфы крови - гордая, надменная раса, предками которой являются легендарные высшие эльфы. Они тяготеют к солнцу и магии, именно поэтому их имена нередко несут в себе нечто волшебное, гордое и неустрашимое. Звучание их имен внушает уверенность в их непоколебимости и верности идеалам своего народа.", slug: "bloodelf", description: "", game: Game.where(slug: 'wow').first},
  {title: "Генератор ников для Пандаренов(РП ники, генератор roleplay ников и имен, панды)", name: "Генератор ников для Пандаренов", content: "Пандарены - древняя раса медведеподобных существ, которые славятся своей приверженностью к пиву, боевым искусствам и самосовершенствованию. Их имена повторяют традиции Востока - каждый слог имеет собственное значение, а если перевести имя пандарена на один из распространенных в Азероте языков, несложно будет оценить всю его красоту.", slug: "pandaren", description: "", game: Game.where(slug: 'wow').first},
  {title: "Асура - Генератор ников для Guild Wars 2", name: "Генератор ников для Асура", content: "Имена представителей расы Асура очень редко бывают длинными. Они составлены из одного или двух слогов и зачастую имеют повторяющиеся согласные. Это справедливо как в отношении женских, так и мужских имен. Имя Асура - одно слово, они не имеют фамилий. Вторым словом может служить должность, профессия или звание, полученное за какие-либо заслуги. Те же, кто все же берет себе второе имя, считаются другими асура эксцентричными чудаками.", slug: "asura", description: "", game: Game.where(slug: 'gw2').first},
  {title: "Сильвари - Генератор ников для Guild Wars 2", name: "Генератор ников для Сильвари", content: "Сильвари - нерожденные дети великого древа, и потому не имеют фамилии. Имя Сильвари звучит прямо и честно, а отсутствие второго имени не дает забыть никому из них - и из окружающих - о том, что каждый Сильвари является частью единой семьи, единого мира и всей Тирии.", slug: "sylvari", description: "", game: Game.where(slug: 'gw2').first},
  {title: "Норн - Генератор ников для Guild Wars 2", name: "Генератор ников для Норнов", content: "Суровый северный народ Норнов издревле брал себе соответствующие характеру и складу души имена. Родители нарекали ребенку имя - а тот должен был прославить его в меру своих способностей. Искусные охотники и любители крепкой выпивки, - а как без неё в холодах? - Норны нередко отличались и на воинском поприще, за что и получали грозные прозвища. Именно эти прозвища со временем и стали их фамилиями.", slug: "norn", description: "", game: Game.where(slug: 'gw2').first},
  {title: "Чарр - Генератор ников для Guild Wars 2", name: "Генератор ников для Чарров", content: "Взращенные в пламени и дыму непрекращающихся войн, Чарры, более всего похожие на воинственных рогатых котов, дают своим детям резкие, но вместе с тем и звучные имена. Фамилии - вторые имена - чарров являются так же их прозвищами. Если кот отличается легкой походкой, не оставляющей следов на снегу, вероятнее всего, что товарищи его так и прозовут - Тихоступ.", slug: "charr", description: "", game: Game.where(slug: 'gw2').first},
  {title: "Человек - Генератор ников для для персонажей Guild Wars 2", name: "Генератор ников для Людей", content: "Имя Человека привычно слуху большинства рас Тирии. Имена бывают короткие и длинные, исполненные значимости и просто имена. В качестве второй части имени люди используют фамилию - она указывает на принадлежность к определенной семье, а, поскольку семья для этой расы имеет немалое значение, то и фамилии носят практически все её представители.", slug: "human", description: "", game: Game.where(slug: 'gw2').first},
  {title: "Ники для Minecraft, Minecraft ники по скинам, мужские имена для Minecraft", name: "Ники для Майнкрафт", content: "Ники для Minecraft могут быть какими угодно. Тем не менее, многие сервера требуют, чтобы имя соответствовало атмосфере игры. Игрок должен создать для своего персонажа Minecraft ролевой ник или имя. На этой странице вы можете создать множество ников для мальчиков. Просто нажимайте кнопку \"Создать имя\" и выбирайте то, что вам нравится.", slug: "man", description: "", game: Game.where(slug: 'minecraft').first},
  {title: "Ники для девушек Minecraft, Minecraft ники по скинам, имена для девочек Minecraft", name: "Ники для Майнкрафт для девушек", content: "Minecraft - удивительная игра, и если играть в неё с друзьями, важно правильно подобрать имя. При игре в Minecraft ники для девочек, а точнее их поиск, могут доставить некоторые проблемы. Ники для девушек в Minecraft должны отражать их сущность, характер и личность. Если вы хотите создать или придумать женский ник или имя для игры в Minecraft, просто нажимайте кнопку и забирайте понравившееся имя.", slug: "woman", description: "", game: Game.where(slug: 'minecraft').first},
  {title: "Красивые немецкие имена - генератор имен и ников для SAMP", name: "Красивые немецкие имена и фамилии", content: "Немецкие имена известны своей громкостью и некоторой грубостью в звучании. Однако, красивые женские имена и фамилии вовсе не редкость в Германии. Так же, как и мужские, они имеют некоторую жесткость, но эта жесткость так же придает и уникальность звучанию, не говоря уже о собственном очаровании и шарме. Генератор ников позволит создать красивое немецкое имя и фамилию для вашего персонажа в SAMP.", slug: "german", description: "", game: Game.where(slug: 'samp').first},
  {title: "Красивые французские имена и фамилии - генератор ников для SAMP", name: "Красивые французские имена - генератор имен и ников для SAMP", content: "Известно, что французские имена и фамилии - одни из самых красивых в мире. Французы и француженки говорят на легком, воздушном языке, который не допускает грубого произношения. Поэтому красивые французские имена и фамилии свойственны как мужчинам, так и женщинам. На этой странице очень легко создать французское имя для игры в SAMP.", slug: "french", description: "", game: Game.where(slug: 'samp').first},
  {title: "Красивые итальянские имена и фамилии - генератор ников для SAMP", name: "Красивые итальянские имена и фамилии", content: "Итальянцы - темпераментная и яркая нация. История и традиции этого народа сделала вытачивала имена несколько столетий. Красивые итальянские имена и фамилии - совсем не редкость (как мужские, так и женские), хотя для самих итальянцев это обычное дело. Создайте красивую фамилию для San Andreas Multiplayer и погрузитесь в игру как настоящий итальянец!", slug: "italian", description: "", game: Game.where(slug: 'samp').first},
  {title: "Красивые датские имена и фамилии - генератор ников для SAMP", name: "Красивые датские имена и фамилии", content: "Дания - родина одного великого сказочника со звучным и громким именем. Датские имена сохраняют в себе традиционное произношение и меняются не так быстро, как имена иных народов. Создайте красивое мужское или женское имя и фамилию для игры в GTA San Andreas Multiplayer и отыгрывайте роль датчанина в мире SAMP.", slug: "danish", description: "", game: Game.where(slug: 'samp').first},
  {title: "Красивые шведские имена и фамилии - генератор ников для SAMP", name: "Красивые шведские имена и фамилии", content: "Шведы - северный народ, в их именах слышны отзвуки старинных традиций и морозной зимы. Красивые шведские имена составляют примерно половину от всех имен, присущих жителям Скандинавии. То же самое можно сказать и о красивых шведских фамилиях. Здесь легко сгенерировать красивое мужское или женское шведское имя и фамилию для игры в SAMP.", slug: "swedish", description: "", game: Game.where(slug: 'samp').first},
  {title: "Красивые испанские имена и фамилии - генератор ников для SAMP", name: "Красивые испанские имена и фамилии", content: "Страстные испанцы зачастую обладают несколько резкими для непривычного человека именами. Однако, они не лишени своеобразной красоты, и если хоть на секунду задуматься, испанские имена такие же красивые, как и у других народов. Красивые испанские фамилии встречаются не так часто, но и их достаточно много, чтобы сгенерировать мужское (или женское) имя и фамилию для GTA San Andreas Multiplayer.", slug: "spanish", description: "", game: Game.where(slug: 'samp').first},
  {title: "Красивые английские имена - генератор имен и ников для SAMP", name: "Красивые английские(американские) имена и фамилии", content: "Жители туманного Альбиона и всех Соединенных Королевств отличаются прекрасными старинными именами. Красивые английские имена и фамилии являются их достоянием, многие семья ведут свой род со средних веков и по сей день сохраняют красивые фамилии. Создайте себе английское или американское (мужское или женское) имя и фамилию для San Andreas Multiplayer.", slug: "english", description: "", game: Game.where(slug: 'samp').first}
